mapper.select:
SELECT * FROM MCO_FILES A WHERE 1=1
AND A.GROUP_KEY = #{GROUP_KEY}
AND A.FILE_KEY = #{FILE_KEY}

SELECT BNO , (SELECT BNO FROM BOARD WHERE BNO > #{bno} AND DEL = 'N'
AND KIND = #{kind}
ORDER BY BNO LIMIT 1) AS PRECONTENT , (SELECT BNO FROM BOARD WHERE BNO < #{bno} AND DEL = 'N'
AND KIND = #{kind}
ORDER BY BNO DESC LIMIT 1) AS NEXTCONTENT , TITLE , DATE_FORMAT(A.REG_DATE, '%Y.%m.%d. %H:%i') AS REG_DATE , CNT , ID , (SELECT COUNT(B.RNO) AS CNT FROM BOARD A , REPLY B WHERE 1=1 AND A.BNO = B.BNO AND A.BNO = #{bno} ) AS REPLYCNT , MENU_NM , CONTENT , UPD_SEQ , TYPE , KIND FROM BOARD AS A JOIN MENU AS B ON A.KIND = B.MENU_ID WHERE 1=1 AND BNO = #{bno} AND DEL = 'N'
AND KIND = #{kind}

SELECT A.BNO AS BNO , A.TITLE AS TITLE , A.ID AS ID , UNIX_TIMESTAMP(DATE_FORMAT(NOW(), '%Y-%m-%d')) - UNIX_TIMESTAMP(A.REG_DATE) AS GAP , CASE WHEN (UNIX_TIMESTAMP(DATE_FORMAT(NOW(), '%Y-%m-%d')) - UNIX_TIMESTAMP(A.REG_DATE)) < 0 THEN DATE_FORMAT(A.REG_DATE, '%H:%i') ELSE DATE_FORMAT(A.REG_DATE, '%Y.%m.%d.') END AS REG_DATE
, A.CNT AS CNT , C.CNT AS GOOD , B.CNT AS REPLYCNT , A.KIND , D.MENU_NM FROM BOARD AS A JOIN (SELECT A.BNO AS BNO , COUNT(B.RNO) AS CNT FROM BOARD AS A LEFT OUTER JOIN REPLY AS B ON A.BNO = B.BNO WHERE 1=1 GROUP BY A.BNO) AS B ON A.BNO = B.BNO JOIN (SELECT A.BNO AS BNO , COUNT(B.ID) AS CNT FROM BOARD AS A LEFT OUTER JOIN GOOD AS B ON A.BNO = B.BNO WHERE 1=1 GROUP BY A.BNO) AS C ON A.BNO = C.BNO JOIN MENU D ON A.KIND = D.MENU_ID WHERE 1=1 AND DEL = 'N'
AND KIND = #{kind}
ORDER BY A.BNO DESC LIMIT #{offset}, #{userDisplay}

mapper.insert:
INSERT INTO MCO_FILES ( GROUP_KEY
                      , FILE_KEY
                      , FILE_REALNAME
                      , FILE_NAME
                      , FILE_PATH
                      , FILE_LENGTH
                      , FILE_TYPE
                      , DEL_YN
                      , REG_ID
                      , REG_DATE
                      , CHG_ID
                      , CHG_DATE)
               VALUES ( #{GROUP_KEY}
                      , #{FILE_KEY}
                      , #{FILE_REALNAME}
                      , #{FILE_NAME}
                      , #{FILE_PATH}
                      , #{FILE_LENGTH}
                      , #{FILE_TYPE}
                      , 'N'
                      , #{USER_ID}
                      , SYSDATE
                      , #{USER_ID}
                      , SYSDATE)

INSERT INTO BOARD_HST ( BNO
                      , ID
                      , TITLE
                      , UPD_SEQ
                      , CONTENT
                      , TYPE
                      , CNT
                      , KIND
                      , UPD_DATE )
               VALUES ( #{bno}
                      , #{id}
                      , #{title}
                      , #{upd_seq}
                      , #{content}
                      , #{type}
                      , #{cnt}
                      , #{kind}
                      , NOW(3) )

mapper.update:
UPDATE BOARD
   SET UPD_SEQ = #{upd_seq}
     , TITLE   = #{title}
     , CONTENT = #{content}
     , TYPE    = #{type}
     , KIND    = #{kind}
 WHERE 1=1
   AND BNO = #{bno}
