mapper.select:
SELECT * FROM MCO_FILES A WHERE 1=1
AND A.GROUP_KEY = #{GROUP_KEY}
AND A.FILE_KEY = #{FILE_KEY}

mapper.insert:
INSERT INTO MCO_FILES ( GROUP_KEY
                      , FILE_KEY
                      , FILE_REALNAME
                      , FILE_NAME
                      , FILE_PATH
                      , FILE_LENGTH
                      , FILE_TYPE
                      , DEL_YN
                      , REG_ID
                      , REG_DATE
                      , CHG_ID
                      , CHG_DATE)
               VALUES ( #{GROUP_KEY}
                      , #{FILE_KEY}
                      , #{FILE_REALNAME}
                      , #{FILE_NAME}
                      , #{FILE_PATH}
                      , #{FILE_LENGTH}
                      , #{FILE_TYPE}
                      , 'N'
                      , #{USER_ID}
                      , SYSDATE
                      , #{USER_ID}
                      , SYSDATE)
