mapper.select:
SELECT * FROM MCO_FILES A WHERE 1=1
AND A.GROUP_KEY = #{GROUP_KEY}
AND A.FILE_KEY = #{FILE_KEY}

SELECT BNO , (SELECT BNO FROM BOARD C WHERE BNO > #{bno} AND DEL = 'N'
AND KIND = #{kind}
AND (CASE WHEN C.KIND = 'BN' THEN C.TYPE = 'N' ELSE A.TYPE IN ('Y', 'N') END)
ORDER BY BNO LIMIT 1) AS PRECONTENT , (SELECT BNO FROM BOARD C WHERE BNO < #{bno} AND DEL = 'N'
AND KIND = #{kind}
AND (CASE WHEN C.KIND = 'BN' THEN C.TYPE = 'N' ELSE A.TYPE IN ('Y', 'N') END)
ORDER BY BNO DESC LIMIT 1) AS NEXTCONTENT , TITLE , DATE_FORMAT(A.REG_DATE, '%Y.%m.%d. %H:%i') AS REG_DATE , CNT , ID , (SELECT COUNT(B.RNO) AS CNT FROM BOARD A , REPLY B WHERE 1=1 AND A.BNO = B.BNO AND A.BNO = #{bno} AND B.DEL_YN = 'N' ) AS REPLYCNT , (SELECT COUNT(B.ID) AS CNT FROM BOARD AS A LEFT OUTER JOIN GOOD AS B ON A.BNO = B.BNO WHERE 1=1 AND A.BNO = #{bno} ) AS GOOD , MENU_NM , CONTENT , UPD_SEQ , TYPE , KIND FROM BOARD AS A JOIN MENU AS B ON A.KIND = B.MENU_ID WHERE 1=1 AND BNO = #{bno} AND DEL = 'N'
AND KIND = #{kind}

SELECT COUNT(A.ID) AS CNT
  FROM GOOD AS A
 WHERE 1=1
   AND A.BNO = #{bno}
   AND A.ID = #{id}

mapper.insert:
INSERT INTO MCO_FILES ( GROUP_KEY
                      , FILE_KEY
                      , FILE_REALNAME
                      , FILE_NAME
                      , FILE_PATH
                      , FILE_LENGTH
                      , FILE_TYPE
                      , DEL_YN
                      , REG_ID
                      , REG_DATE
                      , CHG_ID
                      , CHG_DATE)
               VALUES ( #{GROUP_KEY}
                      , #{FILE_KEY}
                      , #{FILE_REALNAME}
                      , #{FILE_NAME}
                      , #{FILE_PATH}
                      , #{FILE_LENGTH}
                      , #{FILE_TYPE}
                      , 'N'
                      , #{USER_ID}
                      , SYSDATE
                      , #{USER_ID}
                      , SYSDATE)

INSERT INTO BOARD_HST ( BNO
                      , ID
                      , TITLE
                      , UPD_SEQ
                      , CONTENT
                      , TYPE
                      , CNT
                      , KIND
                      , UPD_DATE )
               VALUES ( #{bno}
                      , #{id}
                      , #{title}
                      , #{upd_seq}
                      , #{preContent}
                      , #{type}
                      , #{cnt}
                      , #{kind}
                      , NOW(3) )

INSERT INTO GOOD ( BNO
                 , ID
                 , LIKE_DATE )
          VALUES ( #{bno}
                 , #{id}
                 , NOW(3) )

mapper.update:
UPDATE BOARD
   SET UPD_SEQ = #{upd_seq}
     , TITLE   = #{title}
     , CONTENT = #{content}
     , TYPE    = #{type}
     , KIND    = #{kind}
 WHERE 1=1
   AND BNO = #{bno}

mapper.delete:
DELETE FROM GOOD
 WHERE 1=1
   AND BNO = #{bno}
   AND ID = #{id}
